mapper.select:
SELECT * FROM folder_view
WHERE owner = #{owner}
ORDER BY

    is_stared DESC,

id DESC
LIMIT #{from}, #{itemPerPage}

SELECT * FROM folder_view
WHERE owner = #{owner} AND is_shared = #{isShared}
ORDER BY id DESC
LIMIT #{from}, #{itemPerPage}

SELECT * FROM folder_view
WHERE is_shared = #{isShared}
ORDER BY id DESC
LIMIT #{from}, #{itemPerPage}

SELECT * FROM folder_view
WHERE owner = #{owner} AND is_stared = #{isStared}
ORDER BY id DESC

SELECT * FROM folder_view
WHERE owner != #{owner} AND is_shared = #{isShared}
ORDER BY like_count DESC
LIMIT #{from}, #{itemPerPage}
